"use strict";$("#userid").blur(function(){var s=$(this).val();sendAjax("http://localhost:1023/vivo/server/php/login_check.php",{data:{user:s},success:function(s){1!=s&&0==s&&alert("用户名不存在，请重新输入或先注册")}})}),$(".login_btn").click(function(){var a=$("#userid").val(),e=$("#password").val();sendAjax("http://localhost:1023/vivo/server/php/login_send.php",{data:{user:a,password:e},success:function(s){1==s?(localStorage.user=a,localStorage.password=e,location.href="VIVO-index.html"):0==s&&alert("密码或用户名不正确，请重新输入")}})});